import { Component, Input, OnInit } from "@angular/core";
import { ErabliereApi } from "src/core/erabliereapi.service";
import { Alerte } from "src/model/alerte";
import { UntypedFormGroup, UntypedFormBuilder, UntypedFormControl } from "@angular/forms";
import { AlerteCapteur } from "src/model/alerteCapteur";
import { Capteur } from "src/model/capteur";

@Component({
    selector: 'ajouter-alerte-modal',
    templateUrl: 'ajouter-alerte.component.html'
})
export class AjouterAlerteComponent implements OnInit {

    typeAlerteList = [
        { id: 1, name: 'Alerte trio de donne' },
        { id: 2, name: 'Alerte de capteur' }
    ];

    typeAlerteSelectListForm = new UntypedFormGroup({
        state: new UntypedFormControl(this.typeAlerteList[0].id)
    });
    
    constructor(private _api: ErabliereApi, private fb: UntypedFormBuilder) {
        this.alerteForm = this.fb.group({});
        this.alerteCapteurForm = this.fb.group({});
    }
    
    ngOnInit(): void {
        this.initializeForms();
    }

    initializeForms() {
        this.alerteForm = this.fb.group({
            destinataire: '',
            temperatureMin: '',
            temperatureMax: '',
            vacciumMin: '',
            vacciumMax: '',
            niveauBassinMin: '',
            niveauBassinMax: ''
        });
        this.alerteCapteurForm = this.fb.group({
            destinataire: '',
            min: '',
            max: '',
            idCapteur: ''
        });
    }
    
    display:string = "";

    alerte:Alerte = new Alerte();
    alerteCapteur:AlerteCapteur = new AlerteCapteur();

    @Input() alertes?: Array<Alerte>;
    @Input() alertesCapteur?: Array<AlerteCapteur>;

    @Input() idErabliereSelectionee:any
    capteurs: Array<Capteur> = [];

    alerteForm: UntypedFormGroup;
    alerteCapteurForm: UntypedFormGroup;

    typeAlerte:number = 1;

    onSubmit() {

    }

    onButtonAjouterClick() {
        this.display = "alerte";
    }

    onButtonAnnuleClick() {
        this.display = "";
    }

    onChangeAlerteType(event:any) {
        this.typeAlerte = event.target.value;

        if (this.typeAlerte == 2) {
            this._api.getCapteurs(this.idErabliereSelectionee).then(r => {
                this.capteurs = r;
            });
        }
    }

    onButtonCreerClick() {
        if (this.alerte != undefined) {
            this.alerte.idErabliere = this.idErabliereSelectionee;
            this.alerte.envoyerA = this.alerteForm.controls['destinataire'].value;
            this.alerte.temperatureThresholdLow = this.alerteForm.controls['temperatureMax'].value;
            this.alerte.temperatureThresholdHight = this.alerteForm.controls['temperatureMin'].value;
            this.alerte.vacciumThresholdLow = this.alerteForm.controls['vacciumMax'].value;
            this.alerte.vacciumThresholdHight = this.alerteForm.controls['vacciumMin'].value;
            this.alerte.niveauBassinThresholdLow = this.alerteForm.controls['niveauBassinMax'].value;
            this.alerte.niveauBassinThresholdHight = this.alerteForm.controls['niveauBassinMin'].value;
            this._api.postAlerte(this.idErabliereSelectionee, this.alerte)
                     .then(r => {
                         this.display = "";
                         r.emails = r.envoyerA.split(";");
                         this.alertes?.push(r);
                     });
        }
        else {
            console.log("this.alerte is undefined");
        }
    }

    onButtonCreerAlerteCapteurClick() {
        if (this.alerteCapteur != undefined) {
            this.alerteCapteur.idCapteur = this.alerteCapteurForm.controls['idCapteur'].value;
            this.alerteCapteur.envoyerA = this.alerteCapteurForm.controls['destinataire'].value;
            if (this.alerteCapteurForm.controls['min'].value != "") {
                this.alerteCapteur.minVaue = parseInt(this.alerteCapteurForm.controls['min'].value);
            } else {
                this.alerteCapteur.minVaue = undefined;
            }
            if (this.alerteCapteurForm.controls['max'].value != "") {
                this.alerteCapteur.maxValue = parseInt(this.alerteCapteurForm.controls['max'].value);
            } else {
                this.alerteCapteur.maxValue = undefined;
            }
            this._api.postAlerteCapteur(this.alerteCapteur.idCapteur, this.alerteCapteur)
                     .then(r => {
                         this.display = "";
                         r.emails = r?.envoyerA?.split(";");
                         this.alertesCapteur?.push(r);
                     });
        }
        else {
            console.log("this.alerteCapteur is undefined");
        }
    }
}

import { Component, EventEmitter, Input, OnInit, Output } from "@angular/core";
import { UntypedFormBuilder, UntypedFormGroup } from "@angular/forms";
import { ErabliereApi } from "src/core/erabliereapi.service";
import { PostDonneeCapteur } from "src/model/donneeCapteur";

@Component({
    selector: 'ajouter-donnee-capteur',
    template: `
        <button *ngIf="!display" class="btn btn-primary" (click)="afficherForm()">Ajouter</button>
        <div *ngIf="display" class="border-top">
            <h3>Ajouter une donne</h3>
            <form [formGroup]="donneeCapteurForm">
                <div class="form-group">
                    <span *ngIf="generalErrorMessage" class="text-danger">{{ generalErrorMessage }}</span>
                </div>
                <div class="form-group">
                    <label for="valeur">Valeur</label>
                    <input type="number" class="form-control" id="valeur" name="valeur" placeholder="Valeur" formControlName="valeur">
                    <div *ngIf="this.donneeCapteurForm.controls['valeur'].errors">
                        <span class="text-danger">{{ this.donneeCapteurForm.controls['valeur'].errors.message }}</span>
                    </div>
                </div>
                <div class="form-group">
                    <label for="date">Date</label>
                    <input type="datetime-local" class="form-control" id="date" name="date" placeholder="Date" formControlName="date">
                    <div *ngIf="this.donneeCapteurForm.controls['date'].errors">
                        <span class="text-danger">{{ this.donneeCapteurForm.controls['date'].errors.message }}</span>
                    </div>
                </div>
                <button type="button" class="btn btn-primary" (click)="ajouterDonnee()">Ajouter</button>
                <button type="button" class="btn btn-secondary" (click)="annuler()">Annuler</button>
            </form>
        </div>
    `,
    styles: [`
        .border-top {
            border-top: 1px solid #ccc;
        }
    `]
})
export class AjouterDonneeCapteurComponent implements OnInit {
    @Input() idCapteur: any;
    donneeCapteurForm: UntypedFormGroup;
    display: boolean = false;
    generalErrorMessage: string | null = null;

    @Output() needToUpdate = new EventEmitter();

    constructor(private api: ErabliereApi, private fb: UntypedFormBuilder) {
        this.donneeCapteurForm = this.fb.group({});
     }

    ngOnInit(): void {
        this.donneeCapteurForm = this.fb.group({
            valeur: '',
            date: ''
        });
    }

    onSubmit() {

    }

    ajouterDonnee() {
        this.generalErrorMessage = null;
        var donneeCapteur = new PostDonneeCapteur();
        var validationError = false;
        try {
            donneeCapteur.v = parseInt(this.donneeCapteurForm.controls['valeur'].value);
        } catch (error) {
            this.donneeCapteurForm.controls['valeur'].setErrors({
                'incorrect': true,
                'message': 'Impossible de convertir la valeur en entier'
            })
            validationError = true;
        }
        try {
            donneeCapteur.d = new Date(this.donneeCapteurForm.controls['date'].value).toISOString();
        } catch (error) {
            this.donneeCapteurForm.controls['date'].setErrors({
                'incorrect': true,
                'message': "Impossible d'interpreter la date"
            })
            validationError = true;
        }
        
        if (validationError == false) {
            donneeCapteur.idCapteur = this.idCapteur;

            this.api.postDonneeCapteur(this.idCapteur, donneeCapteur).then(() => {
                this.donneeCapteurForm.reset();
                this.needToUpdate.emit();
            }).catch(e => {
                if (e.status == 400) {
                    if (e.error.errors.V != undefined) {
                        this.donneeCapteurForm.controls['valeur'].setErrors({
                            'incorrect': true,
                            'message': e.error.errors.V.join(', ')
                        })
                    }
                    if (e.error.errors['$.D'] != undefined) {
                        this.donneeCapteurForm.controls['date'].setErrors({
                            'incorrect': true,
                            'message': e.error.errors['$.D'].join(', ')
                        })
                    }
                }
                else {
                    this.generalErrorMessage = "Une erreur est survenue lors de l'ajout de la donne";
                }
            });
        }
    }

    annuler() {
        this.donneeCapteurForm.reset();
        this.display = false;
    }

    afficherForm() {
        this.display = true;
    }
}

