import { Component, Input, OnInit, ViewChild } from '@angular/core';
import { ChartDataSets, ChartType } from 'chart.js';
import { Label } from 'ng2-charts';
import { Subject } from 'rxjs';
import { ErabliereApi } from 'src/core/erabliereapi.service';
import { Erabliere } from 'src/model/erabliere';
import { GraphPannelComponent } from './sub-panel/graphpanel.component';

@Component({
    selector: 'donnees-panel',
    template: `
        <div class="border-top">
          <div class="row">
            <div *ngIf="initialErabliere?.afficherTrioDonnees" class="col-md-6">
              <graph-panel [titre]="titre_temperature" 
                           [valeurActuel]="temperatureValueActuel"
                           [symbole]="temperatureSymbole"
                           [timeaxes]="timeaxes" 
                           (updateGraphCallback)="updateGraph($event)"
                           (updateGraphUsingFixRangeCallback)="updateGraphUsingFixRangeCallback($event)"
                           [datasets]="temperature" #temperatureGraphPannel></graph-panel>
            </div>
            <div *ngIf="initialErabliere?.afficherTrioDonnees" class="col-md-6">
              <graph-panel [titre]="titre_vaccium" 
                           [valeurActuel]="vacciumValueActuel"
                           [symbole]="vacciumSymbole"
                           [timeaxes]="timeaxes" 
                           (updateGraphCallback)="updateGraph($event)"
                           (updateGraphUsingFixRangeCallback)="updateGraphUsingFixRangeCallback($event)"
                           [datasets]="vaccium" #vacciumGraphPannel></graph-panel>
            </div>
            <div *ngIf="initialErabliere?.afficherTrioDonnees" class="col-md-6">
              <graph-panel [titre]="titre_niveaubassin" 
                           [valeurActuel]="niveauBassinValueActuel"
                           [symbole]="niveauBassinSymbole"
                           [timeaxes]="timeaxes" 
                           (updateGraphCallback)="updateGraph($event)"
                           (updateGraphUsingFixRangeCallback)="updateGraphUsingFixRangeCallback($event)"
                           [datasets]="niveaubassin" #niveaubassinGraphPannel></graph-panel>
            </div>
            <div *ngIf="initialErabliere?.afficherSectionDompeux" class="col-md-6">
              <bar-panel [titre]="titre_dompeux" 
                         [timeaxes]="timeaxes_dompeux" 
                         [datasets]="dompeux"
                         [barChartType]="dompeux_line_type" #dompeuxGraphPannel></bar-panel>
            <div>
          </div>
        </div>
    `
})
export class DonneesComponent implements OnInit {
      @ViewChild('temperatureGraphPannel') temperatureGraphPannel?: GraphPannelComponent
      @ViewChild('vacciumGraphPannel') vacciumGraphPannel?: GraphPannelComponent
      @ViewChild('niveaubassinGraphPannel') niveaubassinGraphPannel?: GraphPannelComponent
      @ViewChild('dompeuxGraphPannel') dompeuxGraphPannel?: GraphPannelComponent

      intervalRequetes?:any

      @Input() initialErabliere?: Erabliere
      @Input() erabliereSubject: Subject<Erabliere> = new Subject<Erabliere>()
      @Input() dureeDonneesRequete:any

      timeaxes: Label[] = [];
      timeaxes_dompeux: Label[] = []

      derniereDonneeRecu?:string = undefined;
      ddr?:string = undefined;
      dernierDompeuxRecu?:string = undefined;
      ddrDompeux?:string = undefined;

      ids:Array<number> = []
      idsDompeux:Array<number> = []

      titre_temperature = "Temperature"
      temperature: ChartDataSets[] = []
      temperatureValueActuel?:string|null
      temperatureSymbole:string = "c"

      titre_vaccium = "Vaccium"
      vaccium: ChartDataSets[] = []
      vacciumValueActuel?:string|null
      vacciumSymbole:string = "HG"
      vacciumYOption:any = [
          {
            display: true,
            ticks: { 
                beginAtZero: true, 
                max: 30
            }
        }
      ]

      titre_niveaubassin = "Niveau Bassin"
      niveaubassin: ChartDataSets[] = []
      niveauBassinValueActuel?:string|null
      niveauBassinSymbole:string = "%"

      titre_dompeux = "Dompeux"
      dompeux: ChartDataSets[] = []
      dompeux_line_type: ChartType = "bar"
      dompeux_chart_type:string = "bar"

      erabliereAfficherTrioDonnees: boolean | undefined;
      erabliereAfficherSectionDompeux: boolean | undefined;
      erabliereId: any;

      constructor(private _erabliereApi:ErabliereApi){ }

      ngOnInit() {
        this.erabliereSubject.subscribe(response => {
          this.ngOnDestroy();
          this.ddr = undefined;
          this.ddrDompeux = undefined;
          this.dernierDompeuxRecu = undefined;
          this.derniereDonneeRecu = undefined;

          if (response != undefined) {
            this.erabliereAfficherTrioDonnees = response.afficherTrioDonnees;
            this.erabliereAfficherSectionDompeux = response.afficherSectionDompeux;
            this.erabliereId = response.id;
          }
          else {
            this.erabliereAfficherTrioDonnees = undefined;
            this.erabliereAfficherSectionDompeux = undefined;
            this.erabliereId = undefined;
          }

          this.fetchDataAndBuildGraph()
        });

        this.erabliereAfficherTrioDonnees = this.initialErabliere?.afficherTrioDonnees;
        this.erabliereAfficherSectionDompeux = this.initialErabliere?.afficherSectionDompeux;
        this.erabliereId = this.initialErabliere?.id;

        this.fetchDataAndBuildGraph();
      }

      fetchDataAndBuildGraph() {
        if (this.erabliereAfficherTrioDonnees == true) {
          this.doHttpCall();
        }
        if (this.erabliereAfficherSectionDompeux == true) {
          this.doHttpCallDompeux();
        }

        this.intervalRequetes = setInterval(() => {
          if (this.fixRange == false) {
            if (this.erabliereAfficherTrioDonnees == true) {
              this.doHttpCall();
            }
          }
          if (this.erabliereAfficherSectionDompeux == true) {
            this.doHttpCallDompeux();
          }
        }, 1000 * 60);
      }

      ngOnDestroy() {
        clearInterval(this.intervalRequetes);
      }

      doHttpCallDompeux() {
        let debutFiltre = this.obtenirDebutFiltre().toISOString();
        let finFiltre = new Date().toISOString();

        var xddr = null;
        if (this.dernierDompeuxRecu != undefined) {
          xddr = this.dernierDompeuxRecu.toString();
        }

        this._erabliereApi.getDompeux(this.erabliereId, debutFiltre, finFiltre, xddr)
        .then(resp=> {
          const h = resp.headers;

          this.dernierDompeuxRecu = h.get("x-dde")?.valueOf();
          this.ddrDompeux = h.get("x-ddr")?.valueOf();

          var e = resp.body;

          if (e == null) {
            return;
          }
        
            let idsDompeux = e.map(ee => ee.id);

            let dompeux = [
              { data: e.map(ee => (new Date(ee.df).getTime() - new Date(ee.dd).getTime()) / 1000), label: 'Dure en seconde' }
            ];

            let timeaxes_dompeux = e.map(ee => new Date(ee.t).toLocaleTimeString());

            if (h.has("x-ddr") && this.ddrDompeux != undefined && h.get("x-ddr")?.valueOf() == this.ddrDompeux) {
            
              if (idsDompeux.length > 0 && this.idsDompeux[this.idsDompeux.length - 1] === idsDompeux[0]) {
                dompeux[0].data.shift();
                timeaxes_dompeux.shift();
                idsDompeux.shift();
              }
              
              dompeux[0].data.forEach((d:number) => this.dompeux[0].data?.push(d));
              timeaxes_dompeux.forEach((t: Label) => this.timeaxes_dompeux?.push(t));
              idsDompeux.forEach((n: number) => this.idsDompeux.push(n));

              while (this.timeaxes_dompeux.length > 0 &&
                new Date(this.timeaxes_dompeux[0].toString()) < new Date(debutFiltre)) {
                this.timeaxes_dompeux.shift();
                this.dompeux[0].data?.shift();
                this.idsDompeux.shift();
              }
            }
            else {
              this.dompeux = dompeux;
              this.timeaxes_dompeux = timeaxes_dompeux;
              this.idsDompeux = idsDompeux;
            }

            this.dompeuxGraphPannel?.chart?.update();
        });
      }

      doHttpCall() {
        let debutFiltre = this.obtenirDebutFiltre().toISOString();
        let finFiltre = new Date().toISOString();

        if (this.fixRange) {
          debutFiltre = this.dateDebutFixRange;
          finFiltre = this.dateFinFixRange;
        }

        var xddr = null;
        if (this.derniereDonneeRecu != undefined) {
          xddr = this.derniereDonneeRecu.toString();
        }

        this._erabliereApi.getDonnees(this.erabliereId, debutFiltre, finFiltre, xddr)
          .then(reponse => {
            var h = reponse.headers;

            this.derniereDonneeRecu = h.get("x-dde")?.valueOf();
            this.ddr = h.get("x-ddr")?.valueOf();

            var e = reponse.body;

            if (e == null) {
              return;
            }

            let ids = e.map(ee => ee.id);

            let temperature = [
              { data: e.map(ee => ee.t != null ? ee.t / 10 : null), label: 'Temperature' }
            ];
            let vaccium = [
              { data: e.map(ee => ee.v != null ? ee.v / 10 : null), label: 'Vaccium' }
            ];
            let niveaubassin = [
              { data: e.map(ee => ee.nb), label: 'Niveau bassin' }
            ];

            let timeaxes = e.map(ee => ee.d);

            if (e.length > 0) {
              var tva = e[e.length - 1].t;
              var vva = e[e.length - 1].v;
              this.temperatureValueActuel = tva != null ? (tva / 10).toFixed(1) : null;
              this.vacciumValueActuel = vva != null ? (vva / 10).toFixed(1) : null;
              this.niveauBassinValueActuel = e[e.length - 1].nb?.toString();
            }
            else if (e.length == 0) {
              this.temperatureValueActuel = null;
              this.vacciumValueActuel = null;
              this.niveauBassinValueActuel = null;
            }

            if (h.has("x-ddr") && this.ddr != undefined && h.get("x-ddr")?.valueOf() == this.ddr) {
              
              if (ids.length > 0 && this.ids[this.ids.length - 1] === ids[0]) {
                this.temperature[0].data?.pop();
                this.vaccium[0].data?.pop();
                this.niveaubassin[0].data?.pop();
                this.timeaxes.pop();

                this.temperature[0].data?.push(temperature[0].data.shift() as any);
                this.vaccium[0].data?.push(vaccium[0].data.shift() as any);
                this.niveaubassin[0].data?.push(niveaubassin[0].data.shift() as any);
                this.timeaxes.push(timeaxes.shift() as any);
              }
              
              temperature[0].data.forEach(t => this.temperature[0].data?.push(t as any));
              vaccium[0].data.forEach(v => this.vaccium[0].data?.push(v as any));
              niveaubassin[0].data.forEach(nb => this.niveaubassin[0].data?.push(nb as any));
              timeaxes.forEach(t => this.timeaxes.push(t as any));
              ids.forEach((n: number) => this.ids.push(n));

              while (this.timeaxes.length > 0 &&
                     new Date(this.timeaxes[0].toString()) < new Date(debutFiltre)) {
                this.timeaxes.shift();
                this.temperature[0].data?.shift();
                this.vaccium[0].data?.shift();
                this.niveaubassin[0].data?.shift();
                this.ids.shift();
              }
            }
            else {
              this.temperature = temperature;
              this.vaccium = vaccium;
              this.niveaubassin = niveaubassin;
              this.timeaxes = timeaxes as any[];
              this.ids = ids;
            }

            this.temperatureGraphPannel?.chart?.update();
            this.vacciumGraphPannel?.chart?.update();
            this.niveaubassinGraphPannel?.chart?.update();
          })
          .catch(reason => {
            console.log(reason);
            this.derniereDonneeRecu = undefined;
            this.ddr = undefined;
          });
        }

        duree:string = "12h"
        debutEnHeure: number = 12;

      obtenirDebutFiltre() : Date {
        var twelve_hour = 1000 * 60 * 60 * this.debutEnHeure;

        return new Date(Date.now() - twelve_hour);
      }

      updateGraph($event: any): void {
        this.fixRange = false;
        this.duree = "";

        if ($event.days != 0) {
            this.duree = $event.days + " jours";
        }

        if ($event.hours != 0) {
            this.duree = this.duree + " " + $event.hours + "h";
        }

        this.updateDuree(this.duree);

        this.debutEnHeure = $event.hours + (24 * $event.days);

        this.cleanGraphComponentCache();

        this.doHttpCall();
    }

    updateDuree(duree: string) {
      this.duree = duree;
      this.temperatureGraphPannel?.updateDuree(this.duree);
      this.vacciumGraphPannel?.updateDuree(this.duree);
      this.niveaubassinGraphPannel?.updateDuree(this.duree);
    }

    private cleanGraphComponentCache() {
        this.derniereDonneeRecu = undefined;
        this.ddr = undefined;
        this.ids = [];
    }

    fixRange: boolean = false;
    dateDebutFixRange?: any = undefined
    dateFinFixRange?: any = undefined

    updateGraphUsingFixRangeCallback($event: any): void {
      this.fixRange = true;
      this.dateDebutFixRange = $event.dateDebutFixRange;
      this.dateFinFixRange = $event.dateFinFixRange;
      this.cleanGraphComponentCache();
      this.updateDuree(this.dateDebutFixRange + " - " + this.dateFinFixRange);
      this.doHttpCall();
    }
}

import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { ErabliereApi } from 'src/core/erabliereapi.service';
import { CustomerAccess } from 'src/model/customerAccess';
import { PutCustomerAccess } from 'src/model/putCustomerAccess';

@Component({
    selector: 'edit-access',
    templateUrl: 'edit-access.component.html'
})
export class EditAccessCompoenent implements OnInit {
    @Input() displayEditAccess: Boolean = false;
    @Input() acces: CustomerAccess = new CustomerAccess();
    accessInternal: CustomerAccess = new CustomerAccess();
    @Output() shouldUpdateAccess = new EventEmitter<Boolean>();

    constructor(private _api: ErabliereApi) { }

    ngOnInit() { 
        this.accessInternal = { ...this.acces };
    }

    updateAccess() {
        if (this.acces != undefined) {

            var putModel = new PutCustomerAccess();
            putModel.idErabliere = this.acces.idErabliere;
            putModel.customerErablieres = [];
            putModel.customerErablieres.push({
                idCustomer: this.acces.idCustomer,
                action: 1,
                access: this.accessInternal.access
            });

            this._api.putCustomerAccess(this.acces.idErabliere, putModel).then(() => {
                this.displayEditAccess = false;
                this.shouldUpdateAccess.emit(true);
            }).catch(error => {
                throw error;
            });
        }
    }
}

import { AfterContentChecked, Component, EventEmitter, Input, OnInit, Output, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { AuthorisationFactoryService } from 'src/authorisation/authorisation-factory-service';
import { IAuthorisationSerivce } from 'src/authorisation/iauthorisation-service';
import { ErabliereApi } from 'src/core/erabliereapi.service';
import { Alerte } from 'src/model/alerte';
import { AlerteCapteur } from 'src/model/alerteCapteur';
import { Documentation } from 'src/model/documentation';
import { Erabliere } from 'src/model/erabliere';
import { Note } from 'src/model/note';
import { ModifierErabliereComponent } from './modifier-erabliere.component';

@Component({
  selector: 'erablieres',
  templateUrl: 'erabliere.component.html'
})
export class ErabliereComponent implements OnInit {
  erablieres?: Array<Erabliere>;
  etat: string = "";
  erabliereSelectionnee?: Erabliere;
  idSelectionnee?: any
  @Input() cacheMenuErabliere?: boolean;
  @Input() pageSelectionnee?: number = 0;
  alertes?: Array<Alerte>;
  alertesCapteur?: Array<AlerteCapteur>;
  documentations?: Array<Documentation>;
  notes?: Array<Note>;
  private _authService: IAuthorisationSerivce
  @Output() onAfterRecieveingErablieres: EventEmitter<number> = new EventEmitter<number>();
  loggedIn: Boolean = false;
  authDisabled: boolean = false;

  constructor(private _erabliereApi: ErabliereApi, authFactory: AuthorisationFactoryService, private _router: Router) {
    this.erabliereSelectionnee = undefined;
    this._authService = authFactory.getAuthorisationService();
    if (this._authService.type == "AuthDisabled") {
      this.authDisabled = true;
    }
    this._authService.loginChanged.subscribe(loggedIn => {
      this.loggedIn = loggedIn;
      if (loggedIn) {
        this.loadErablieresPage();
      }
      else {
        this.erablieres = undefined;
        this.erabliereSelectionnee = undefined;
        this.idSelectionnee = undefined;
        this.etat = "Vous n'tes pas connect";
        this.pageSelectionnee = 0;
      }
    });
  }

  async ngOnInit() {
    this.loggedIn = await this._authService.isLoggedIn();
    await this.loadErablieresPage();
  }

  async loadErablieresPage() {
    const titreChargement = "Chargement des rablires...";

    if (this.etat == titreChargement) {
      return new Promise<void>((resolve, reject) => { });
    }

    this.etat = titreChargement;

    const erablieres = await (this._erabliereApi.getErablieresExpandCapteurs(true).catch(err => {
      console.log(err);
      this.etat = "Erreur lors du chargement des rablieres";
    }));

    if (erablieres != null) {
      this.erablieres = erablieres.sort((a, b) => {
        if (a.indiceOrdre != null && b.indiceOrdre == null) {
          return -1;
        }
        else if (b.indiceOrdre != null && a.indiceOrdre == null) {
          return 1;
        }
        else if (a.indiceOrdre != null && b.indiceOrdre != null) {
          return a.indiceOrdre - b.indiceOrdre;
        }

        return a.nom?.localeCompare(b.nom ?? "") ?? 0;
      });

      if (this.erablieres.length > 0) {
        this.onAfterRecieveingErablieres.emit(this.erablieres.length);
        this.etat = "Chargement des erablieres termin";
        this.erabliereSelectionnee = this.erablieres[0];
        this.idSelectionnee = this.erabliereSelectionnee.id;

        // verifier quel est la page selectionnee base sur l'url
        if (this._router.url.indexOf("/alertes") > -1) {
          this.pageSelectionnee = 1;
        }
        else if (this._router.url.indexOf("/documentations") > -1) {
          this.pageSelectionnee = 4;
        }
        else if (this._router.url.indexOf("/notes") > -1) {
          this.pageSelectionnee = 5;
        }
        else if (this._router.url.indexOf("/apropos") > -1) {
          this.pageSelectionnee = 3;
        }
        else {
          this.pageSelectionnee = 0;
        }

        this.handleErabliereLiClick(this.erabliereSelectionnee.id);
      }
      else {
        this.etat = "Aucune erablire";
      }
    }
  }

  handleErabliereLiClick(idErabliere: number) {
    if (this.erablieres == null || this.erablieres == undefined) {
      return;
    }

    this.erabliereSelectionnee = this.erablieres.find(e => e.id === idErabliere);

    this.idSelectionnee = this.erabliereSelectionnee?.id;

    if (this.pageSelectionnee == 1) {
      this.loadAlertes();
    }

    if (this.pageSelectionnee == 4) {
      this.loadDocumentations();
    }

    if (this.pageSelectionnee == 5) {
      this.loadNotes();
    }
  }

  loadAlertes() {
    this._erabliereApi.getAlertes(this.erabliereSelectionnee?.id).then(alertes => {
      this.alertes = alertes;
    });
    this._erabliereApi.getAlertesCapteur(this.erabliereSelectionnee?.id).then(alertesCapteur => {
      this.alertesCapteur = alertesCapteur;
    });
  }

  loadDocumentations() {
    this._erabliereApi.getDocumentations(this.erabliereSelectionnee?.id).then(documentations => {
      this.documentations = documentations;
    });
  }

  loadNotes() {
    this._erabliereApi.getNotes(this.erabliereSelectionnee?.id).then(notes => {
      notes.forEach(n => {
        if (n.fileExtension == 'csv') {
          n.decodedTextFile = atob(n.file ?? "");
        }
      });

      this.notes = notes;
    });
  }

  @ViewChild(ModifierErabliereComponent) modifierErabliereComponent?: ModifierErabliereComponent;

  openEditErabliereForm(erabliere: Erabliere) {
    if (this.modifierErabliereComponent != undefined) {
      if (this.modifierErabliereComponent.erabliereForm != undefined) {
        this.modifierErabliereComponent.erabliereForm.erabliere = { ...erabliere };
        this.modifierErabliereComponent.modifierAccesUtilisateurs?.refreashAccess(erabliere.id);
      }
      else {
        console.log("erabliereForm is undefined");
      }
    }
    else {
      console.log("modifierErabliereComponent is undefined");
    }
  }
}

import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { Erabliere } from 'src/model/erabliere';

@Component({
    selector: 'erabliere-form',
    templateUrl: './erabliere-form.component.html',
})
export class ErabliereFormComponent implements OnInit {
    getDefaultErabliere() {
        let e = new Erabliere();
        e.afficherSectionBaril = true;
        e.afficherSectionDompeux = true;
        e.afficherTrioDonnees = true;
        e.ipRule = "-";
        return e;
    }

    erabliere: Erabliere = this.getDefaultErabliere();
    plusdOptions: boolean = false;
    plusOptionsButtonText: string = "Plus d'options";

    ngOnInit() {
    }

    afficherPlusOptions() {
        this.plusdOptions = !this.plusdOptions;
        if (this.plusdOptions) {
