import { Component, EventEmitter, Input, OnInit, Output, SimpleChange, ViewChild } from '@angular/core';
import { ChartDataSets, ChartOptions, ChartType } from 'chart.js';
import { Color, Label, BaseChartDirective } from 'ng2-charts';
import { ErabliereApi } from 'src/core/erabliereapi.service';

@Component({
    selector: 'graph-panel',
    templateUrl: './graphpanel.component.html',
})
export class GraphPannelComponent implements OnInit {
    @ViewChild(BaseChartDirective) chart?: BaseChartDirective;
    @Input() datasets: ChartDataSets[] = [];
    @Input() timeaxes: Label[] = [];
    @Input() lineChartType = 'line' as ChartType;
    @Input() lineScaleType = 'time'
    @Input() yScaleOption: any = undefined
    lineChartOptions: ChartOptions = {
        responsive: true,
        scales: {
            xAxes: [{
                type: this.lineScaleType,
                ticks: {
                    autoSkip: true,
                    maxTicksLimit: 7
                }
            }]
        }
    };

    lineChartColors: Color[] = [
        {
            borderColor: 'black',
            backgroundColor: 'rgba(255,255,0,0.28)',
        }
    ];

    lineChartLegend = true;
    lineChartPlugins = [];

    @Input() titre: string | undefined = "";
    @Input() valeurActuel?: string | null | number | undefined;
    @Input() symbole: string | undefined;
    @Input() textActuel?: string | undefined | null;
    @Input() ajouterDonneeDepuisInterface: boolean = false;

    constructor(private _api: ErabliereApi) { this.chart = undefined; }

    @Input() idCapteur?: any;

    interval?: any

    ngOnInit(): void {
        if (this.idCapteur != null) {
            this.doHttpCall();

            this.interval = setInterval(() => {
                if (this.fixRange == false) {
                    this.doHttpCall();
                }
            }, 1000 * 60);
        }
    }

    ngOnDestroy() {
        clearInterval(this.interval);
    }

    updateDonneesCapteur(event: any) {
        this.cleanGraphComponentCache();
        this.doHttpCall();
    }

    dernierDonneeRecu?: string = undefined;
    ddr?: string = undefined;

    ids: Array<number> = []

    doHttpCall(): void {
        let debutFiltre = this.obtenirDebutFiltre().toISOString();
        let finFiltre = new Date().toISOString();

        if (this.fixRange) {
            debutFiltre = this.dateDebutFixRange;
            finFiltre = this.dateFinFixRange;
        }

        var xddr = null;
        if (this.dernierDonneeRecu != undefined) {
            xddr = this.dernierDonneeRecu.toString();
        }

        this._api.getDonneesCapteur(this.idCapteur, debutFiltre, finFiltre, xddr).then(resp => {
            const h = resp.headers;

            this.dernierDonneeRecu = h.get("x-dde")?.valueOf();
            this.ddr = h.get("x-ddr")?.valueOf();

            var json = resp.body;

            if (json == null) {
                console.log("donneeCapteur response body was null. Return immediatly");
                return;
            }

            let ids = json.map(ee => ee.id);

            let donnees = [
                { data: json.map(donneeCapteur => donneeCapteur.valeur != null ? donneeCapteur.valeur / 10 : null), label: this.titre }
            ];

            let timeaxes = json.map(donneeCapteur => donneeCapteur.d);

            if (json.length > 0) {
                var actualData = json[json.length - 1];
                var tva = actualData.valeur;
                this.valeurActuel = tva != null ? (tva / 10).toFixed(1) : null;
                this.textActuel = actualData.text;
            }

            if (h.has("x-ddr") && this.ddr != undefined && h.get("x-ddr")?.valueOf() == this.ddr) {

                if (ids.length > 0 && this.ids[this.ids.length - 1] === ids[0]) {
                    this.datasets[0].data?.pop();
                    this.timeaxes.pop();

                    this.datasets[0].data?.push(donnees[0].data.shift() as any);
                    this.timeaxes.push(timeaxes.shift() as any);
                }

                donnees[0].data.forEach(t => this.datasets[0].data?.push(t as any));
                timeaxes.forEach(t => this.timeaxes.push(t as any));
                ids.forEach((n: number) => this.ids.push(n));

                while (this.timeaxes.length > 0 &&
                    new Date(this.timeaxes[0].toString()) < new Date(debutFiltre)) {
                    this.timeaxes.shift();
                    this.datasets[0].data?.shift();
                    this.ids.shift();
                }
            }
            else {
                this.datasets = donnees;
                this.timeaxes = timeaxes as any[];
                this.ids = ids;
            }

            this.chart?.update();
        });
    }

    duree: string = "12h"
    debutEnHeure: number = 12;

    obtenirDebutFiltre(): Date {
        var twelve_hour = 1000 * 60 * 60 * this.debutEnHeure;

        return new Date(Date.now() - twelve_hour);
    }

    @Output() updateGraphCallback = new EventEmitter();
    @Output() updateGraphUsingFixRangeCallback = new EventEmitter();

    updateGraph(days: number, hours: number): void {
        this.fixRange = false;
        if (this.idCapteur != null) {
            this.duree = "";

            if (days != 0) {
                this.duree = days + " jours";
            }

            if (hours != 0) {
                this.duree = this.duree + " " + hours + "h";
            }

            this.debutEnHeure = hours + (24 * days);

            this.cleanGraphComponentCache();

            this.doHttpCall();
        }
        else {
            // When this.idCapteur is null, we are in a component such as 'donnees.component.ts'
            this.updateGraphCallback.emit({ days, hours });
        }
    }

    private cleanGraphComponentCache() {
        this.dernierDonneeRecu = undefined;
        this.ddr = undefined;
        this.ids = [];
    }

    updateDuree(duree: string) {
        this.duree = duree;
    }

    fixRange: boolean = false;

    updateGraphUsingFixRange() {
        this.fixRange = true;
        if (this.idCapteur != null) {
            this.cleanGraphComponentCache();
            this.updateDuree(this.dateDebutFixRange + " - " + this.dateFinFixRange);
            this.doHttpCall();
        }
        else {
            // When this.idCapteur is null, we are in a component such as 'donnees.component.ts'
            var obj = {
                dateDebutFixRange: this.dateDebutFixRange,
                dateFinFixRange: this.dateFinFixRange
            }
            this.updateGraphUsingFixRangeCallback.emit(obj);
        }
    }

    dateDebutFixRange: any = undefined;
    dateFinFixRange: any = undefined;

    captureDateDebut($event: SimpleChange) {
        this.dateDebutFixRange = $event.currentValue;
    }

    captureDateFin($event: SimpleChange) {
        this.dateFinFixRange = $event.currentValue;
    }
}

import { Component, Input, OnInit } from '@angular/core';
import { ErabliereApi } from 'src/core/erabliereapi.service';
import { Customer } from 'src/model/customer';
import { CustomerAccess } from 'src/model/customerAccess';
import { PutCustomerAccess } from 'src/model/putCustomerAccess';

@Component({
    selector: 'modifier-acces-utilisateurs',
    templateUrl: 'modifier-acces-utilisateurs.component.html',
})

export class ModifierAccesUtilisateursComponent implements OnInit {
    customersAccess: CustomerAccess[] = [];
    erreurChargementDroits: Boolean = false;
    erreurAjoutAcces: Boolean = false;
    displayNewLine: Boolean = false;
    displayEditAccess: Boolean = false;
    newCustomerAccess: PutCustomerAccess = new PutCustomerAccess()
    @Input() idErabliere: any;
    constructor(private _api: ErabliereApi) { }

    ngOnInit() { 
        
    }

    refreashAccess(idErabliere: any) {
        this.erreurChargementDroits = false;
        this._api.getCustomersAccess(idErabliere).then(customersAccess => {
            this.customersAccess = customersAccess;
        })
        .catch(error => {
            this.customersAccess = [];
            this.erreurChargementDroits = true;
            throw error;
        });
    }

    addUserAccess() {
        this.displayNewLine = true;
        this.newCustomerAccess = new PutCustomerAccess();
    }

    hideAddUserAccess() {
        this.displayNewLine = false;
    }

    creerAcces() {
        this.erreurAjoutAcces = false;
        this.newCustomerAccess.idErabliere = this.idErabliere;
        this.newCustomerAccess.customerErablieres[0].action = 0;
        this._api.putCustomerAccess(this.idErabliere, this.newCustomerAccess).then(() => {
            this.refreashAccess(this.idErabliere);
            this.displayNewLine = false;
            this.newCustomerAccess = new PutCustomerAccess();
        }).catch(error => {
            this.erreurAjoutAcces = true;
            throw error;
        });
    }

    customerSelected(customer: Customer) {
        console.log("CustomerSelected " + customer.id);
        this.newCustomerAccess.customerErablieres[0].idCustomer = customer.id;
    }

    showEditForm() {
        this.displayEditAccess = true;
    }

    hideEditForm() {
        this.displayEditAccess = false;
    }

    supprimer(customerAccess?: CustomerAccess) {
        if (customerAccess != undefined) {
            this._api.deleteCustomerAccess(customerAccess.idErabliere, customerAccess.id).then(() => {
                this.refreashAccess(customerAccess.idErabliere);
            });
        }
    }

    shouldRefreshAccessAfterUpdate(shouldRefreshAccess: Boolean) {
        this.displayEditAccess = false;
        if (shouldRefreshAccess) {
            this.refreashAccess(this.idErabliere);
        }
    }
}

import { Component, Input, OnInit } from "@angular/core";
import { ErabliereApi } from "src/core/erabliereapi.service";
import { Alerte } from "src/model/alerte";
import { UntypedFormGroup, UntypedFormBuilder } from "@angular/forms";
import { Subject } from "rxjs";
import { AlerteCapteur } from "src/model/alerteCapteur";

@Component({
    selector: 'modifier-alerte-modal',
    templateUrl: 'modifier-alerte.component.html'
})
export class ModifierAlerteComponent implements OnInit {
    constructor(private _api: ErabliereApi, private fb: UntypedFormBuilder) {
        this.alerteForm = this.fb.group({
            id: '',
            destinataire: '',
            temperatureMin: '',
            temperatureMax: '',
            vacciumMin: '',
            vacciumMax: '',
            niveauBassinMin: '',
            niveauBassinMax: '',
            isEnable: ''
        });
        this.alerteCapteurForm = this.fb.group({
            destinataire: '',
            min: '',
            max: '',
            isEnable: ''
        });
    }
    
    ngOnInit(): void {
        let alerte = this.alerte;

        if (alerte != undefined) {
            this.alerteForm.setValue({
                id: alerte.id,
                destinataire: alerte.envoyerA,
                temperatureMin: alerte.temperatureThresholdHight,
                temperatureMax: alerte.temperatureThresholdLow,
                vacciumMin: alerte.vacciumThresholdHight,
                vacciumMax: alerte.vacciumThresholdLow,
                niveauBassinMin: alerte.niveauBassinThresholdHight,
                niveauBassinMax: alerte.niveauBassinThresholdLow,
                isEnable: alerte.isEnable
            });
        }

        let alerteCapteur = this.alerteCapteur;

        if (alerteCapteur != undefined) {
            this.alerteCapteurForm.setValue({
                destinataire: alerteCapteur.envoyerA,
                min: alerteCapteur.minVaue,
                max: alerteCapteur.maxValue,
                isEnable: alerteCapteur.isEnable
            });
        }
    }

    @Input() alerte?:Alerte;
    @Input() alerteCapteur?:AlerteCapteur
    @Input() idErabliereSelectionee:any

    @Input() displayEditFormSubject = new Subject<string>();
    @Input() alerteEditFormSubject = new Subject<Alerte>();
    @Input() alerteCapteurEditFormSubject = new Subject<AlerteCapteur>();

    alerteForm: UntypedFormGroup;
    alerteCapteurForm: UntypedFormGroup;

    @Input() editAlerte: boolean = false;
    @Input() editAlerteCapteur: boolean = false;

    onSubmit() {

    }

    onButtonAnnuleClick() {
        this.displayEditFormSubject.next("");
    }

    onButtonModifierClick() {
        let alerte = new Alerte();

        alerte.id = this.alerteForm.controls['id'].value;
        alerte.idErabliere = this.idErabliereSelectionee;
        alerte.envoyerA = this.alerteForm.controls['destinataire'].value;
        alerte.temperatureThresholdLow = this.alerteForm.controls['temperatureMax'].value;
        alerte.temperatureThresholdHight = this.alerteForm.controls['temperatureMin'].value;
        alerte.vacciumThresholdLow = this.alerteForm.controls['vacciumMax'].value;
        alerte.vacciumThresholdHight = this.alerteForm.controls['vacciumMin'].value;
        alerte.niveauBassinThresholdLow = this.alerteForm.controls['niveauBassinMax'].value;
        alerte.niveauBassinThresholdHight = this.alerteForm.controls['niveauBassinMin'].value;
        alerte.isEnable = this.alerteForm.controls['isEnable'].value;
        this._api.putAlerte(this.idErabliereSelectionee, alerte)
                 .then(r => {
                     this.displayEditFormSubject.next("");
                     this.alerteEditFormSubject.next(r);
                 });
    }

    onButtonModifierAlerteCapteurClick() {
        let alerte = new AlerteCapteur();

        alerte.id = this.alerteCapteur?.id;
        alerte.idCapteur = this.alerteCapteur?.idCapteur;
        alerte.envoyerA = this.alerteCapteurForm.controls['destinataire'].value;
        alerte.isEnable = this.alerteCapteurForm.controls['isEnable'].value;
        if (this.alerteCapteurForm.controls['min'].value != "") {
            alerte.minVaue = parseInt(this.alerteCapteurForm.controls['min'].value);
        } else {
            alerte.minVaue = undefined;
        }
        if (this.alerteCapteurForm.controls['max'].value != "") {
            alerte.maxValue = parseInt(this.alerteCapteurForm.controls['max'].value);
        } else {
            alerte.maxValue = undefined;
        }
        this._api.putAlerteCapteur(alerte.idCapteur, alerte)
                 .then(r => {
                     this.displayEditFormSubject.next("");
                     this.alerteCapteurEditFormSubject.next(r);
                 });
    }
}

import { Component, OnInit, Input, Output, EventEmitter, ViewChild } from '@angular/core';
import { ErabliereApi } from 'src/core/erabliereapi.service';
import { ErabliereFormComponent } from 'src/erablieres/erabliere-form.component'
import { ModifierAccesUtilisateursComponent } from './modifier-acces-utilisateurs.component';

@Component({
    selector: 'modifier-erabliere',
    templateUrl: './modifier-erabliere.component.html',
})
export class ModifierErabliereComponent implements OnInit {
    @ViewChild(ErabliereFormComponent) erabliereForm?: ErabliereFormComponent;
    @ViewChild(ModifierAccesUtilisateursComponent) modifierAccesUtilisateurs?: ModifierAccesUtilisateursComponent;
    modalTitle: string = "Modifier une rablire";
    @Output() shouldReloadErablieres = new EventEmitter();
    @Input() authEnabled: Boolean = false;
    @Input() idErabliere?: any;
    afficherSectionDeleteErabliere: boolean = false;

    constructor(private _api: ErabliereApi) { }

    ngOnInit() {
        this.afficherSectionDeleteErabliere = false;
    }

    modifierErabliere() {
        if (this.erabliereForm != undefined) {
            if (this.erabliereForm.erabliere != undefined) {
                let erabliere = this.erabliereForm.erabliere;

                this._api.putErabliere(erabliere).then(() => {
                    this.shouldReloadErablieres.emit();
                });
            }
        }
    }

    showDeleteErabliere() {
        this.afficherSectionDeleteErabliere = true;
    }

    deleteErabliere() {
        if (confirm("Voulez-vous vraiment supprimer cette rablire ? Cette action est irrversible.")) {
            var erabliere = this.erabliereForm?.erabliere;
            if (erabliere != undefined) {
                this._api.deleteErabliere(this.idErabliere, erabliere).then(() => {
                    this.afficherSectionDeleteErabliere = true;
                    this.shouldReloadErablieres.emit({event: 'delete'});
                });
            }
        }
    }

    onHideModal() {
        this.afficherSectionDeleteErabliere = false;
    }
}

